record.field:
// get data source
let data_source = this.get_data_source()
let measures = data_source.get_measures()
if (measures.length <= 0) {
    return
}

// get categories
let categories = data_source.get_categories()
if (categories.length <= 0) {
    return
}

// get series
let series = []
let records = data_source.get_records()
let datas = []
for (let i = 0; i < records.length; i++) {
    let record = records[i]
    let item = {
        name: record.get_value(categories[0]),
        value: record.get_value(measures[0])
    }
    datas.push(item)
}

let option = {
  title: {
    text: 'Funnel'
  },
  tooltip: {
    trigger: 'item',
    formatter: '{a} <br/>{b} : {c}%'
  },
  toolbox: {
    feature: {
      dataView: { readOnly: false },
      restore: {},
      saveAsImage: {}
    }
  },
  legend: {
    data: data_source.get_col_values(categories[0])
  },
  series: [
    {
      name: categories[0],
      type: 'funnel',
      left: '10%',
      top: 60,
      bottom: 60,
      width: '80%',
      min: 0,
      max: 100,
      minSize: '0%',
      maxSize: '100%',
      sort: 'descending',
      gap: 2,
      label: {
        show: true,
        position: 'inside'
      },
      labelLine: {
        length: 10,
        lineStyle: {
          width: 1,
          type: 'solid'
        }
      },
      itemStyle: {
        borderColor: '#fff',
        borderWidth: 1
      },
      emphasis: {
        label: {
          fontSize: 20
        }
      },
      data: datas
    }
  ]
};

this.setOption(option)
