record.field:
let data_sources = this.get_data_sources()
if (data_sources.length > 2) {

    // get datas
	let data_source1 = data_sources[0]
    let data_source2 = data_sources[1]
    let data_source3 = data_sources[2]

    if (!data_source1 || !data_source2 || !data_source3) {
        return
    }

	let val1 = data_source1.get_first_aggregate_column_value()
	let val2 = data_source2.get_first_aggregate_column_value()
    let target = data_source3.get_first_aggregate_column_value()
	let percent = (val1 / val2) * 100
    let target_finished = (val1 / target) * 100
    let kpi_percent = (val1 - val2) / val2 * 100

    // set progress bar
    let progress_bar = this.$el.find('.progress-bar')
    progress_bar.attr('aria-valuenow', val1)
    progress_bar.attr('aria-valuemax', val2)
    progress_bar.css('width', percent + '%')

    // kpi percent
    let kpi_percent_el = this.$el.find('.kpi_percent')
    kpi_percent_el.text('(' + kpi_percent + '%)')

    // month_total
    this.$el.find('.month_total').text(val1 / 1000 + 'K')

    // target finished
    this.$el.find('.target_finished').text(target_finished + '%')
}

                
