var category = [];

var dottedBase = +new Date();
dottedBase-=3600 * 24 * 1000*20;

var lineData = [];
var barData = [];

var msgCnt = [
    {msg:100,alm:20},
    {msg:200,alm:40},
    {msg:300,alm:50},
    {msg:400,alm:35},
    {msg:400,alm:40},
    {msg:400,alm:11},
    {msg:400,alm:66},
    {msg:100,alm:77},
    {msg:200,alm:88},
    {msg:300,alm:22},
    {msg:400,alm:99},
    {msg:400,alm:100},
    {msg:400,alm:111},
    {msg:400,alm:222},
    {msg:100,alm:333},
    {msg:200,alm:11},
    {msg:300,alm:33},
    {msg:400,alm:55},
    {msg:400,alm:77},
    {msg:400,alm:90}
]

for (var i = 0; i < 20; i++) {
    var date = new Date(dottedBase += 3600 * 24 * 1000);
    category.push([
        date.getFullYear(),
        date.getMonth() + 1,
        date.getDate()
    ].join('-'));
    var b = msgCnt[i].msg;
    var d = msgCnt[i].alm;
    barData.push(b)
    lineData.push(d + b);
}

