var option = {
    tooltip: {
        trigger: 'item'
    },
    legend: {
        orient: 'vertical',
        x: 'left',
        y: 'bottom',
        data: [
            '已安装设备'
        ],
        textStyle: {
            color: '#ccc'
        }
    },
    visualMap: {
        min: 0,
        max: 2500,
        left: 'right',
        top: 'bottom',
        text: ['高', '低'], // 文本，默认为数值文本
        calculable: true,
        //		color: ['#26cfe4', '#f2b600', '#ec5845'],
        textStyle: {
            color: '#fff'
        }
    },
    series: [{
            name: '已安装设备',
            type: 'map',
            aspectScale: 0.75,
            zoom: 1.2,
            mapType: 'china',
            roam: false,
            label: {
                normal: {
                    show: true,//显示省份标签
                    textStyle:{color:"#c71585"}//省份标签字体颜色
                },    
                emphasis: {//对应的鼠标悬浮效果
                    show: true,
                    textStyle:{color:"#800080"}
                } 
            },
            itemStyle: {
                normal: {
                    borderWidth: .5,//区域边框宽度
                    borderColor: '#009fe8',//区域边框颜色
                    areaColor:"#ffffff",//区域颜色
                },
                emphasis: {
                    borderWidth: .5,
                    borderColor: '#4b0082',
                    areaColor:"#ffdead",
                }
            },
            data: function() {
                var serie = [];
                for(var i = 0; i < map.length; i++) {
                    var item = {
                        name: map[i].area,
                        value: map[i].cnt
                    };
                    serie.push(item);
                }
                return serie;
            }()
        }
    ]
};
this.setOption(option);

                
